const responseText = $input.first().json.output;

// 1. Extract JSON block (robust match)
let extractedJson = null;
let summaryOnly = responseText;
try {
  const jsonMatch = responseText.match(/{[\s\S]*}/);
  if (jsonMatch) {
    const jsonString = jsonMatch[0];
    extractedJson = JSON.parse(jsonString);
    summaryOnly = responseText.replace(jsonString, "").trim(); // strip JSON from display
  }
} catch (err) {
  extractedJson = null;
}

// 2. Validate required fields
let missingFields = [];
let isValid = true;

const campaign = extractedJson?.campaign;
const phases = extractedJson?.campaign_phases;
const declaredPhaseCount = campaign?.number_of_phases;
const actualPhaseCount = Array.isArray(phases) ? phases.length : 0;

// Campaign-level validation
const requiredCampaignFields = [
  "name", "description", "campaign_type", "status",
  "start_date", "end_date", "budget", "target_audience",
  "goals", "target_metrics", "platforms", "number_of_phases"
];
requiredCampaignFields.forEach(field => {
  if (!campaign?.[field]) {
    missingFields.push(`campaign.${field}`);
    isValid = false;
  }
});

// Phase count consistency
if (declaredPhaseCount !== actualPhaseCount) {
  missingFields.push(`Mismatch: campaign.number_of_phases (${declaredPhaseCount}) vs campaign_phases.length (${actualPhaseCount})`);
  isValid = false;
}

// Phase-level validation
if (Array.isArray(phases)) {
  phases.forEach((phase, index) => {
    const requiredPhaseFields = [
      "name", "description", "phase_order", "start_date", "end_date",
      "status", "goals", "target_metrics"
    ];
    requiredPhaseFields.forEach(field => {
      if (!phase?.[field]) {
        missingFields.push(`campaign_phases[${index}].${field}`);
        isValid = false;
      }
    });
  });
}

// 3. Readiness check: valid JSON structure with campaign + phases
const hasCampaignNode = !!campaign;
const hasPhasesArray = Array.isArray(phases) && phases.length > 0;
const readyToSave = hasCampaignNode && hasPhasesArray;

return {
  json: {
    readyToSave,
    campaignPayload: extractedJson || null,
    summaryText: summaryOnly,
    missingFields,
    campaignPhaseCount: actualPhaseCount,
    isValidJSON: isValid
  }
};
